import { useState } from "react";
import { FaSearch } from "react-icons/fa";
import axios from "axios"


const SearchBar = () => {
    let [input, setInput] = useState("")
    let [stockInfo, setStockInfo] = useState("")

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log(input)

        try {
            // Send request to the backend
            const response = await axios.get('http://localhost:8000/api/get-stock-info/', {
                params: { input: input } // Sending the input as a query parameter
            });

            // Log the response data
            let info_dict = response.data.info
            setStockInfo(info_dict)
        } catch (error) {
            console.error("Error fetching stock info:", error);
            setStockInfo("Invalid Query")
        }

        // send request to the backend, retrieve json response with stock info
    }

    return (
        <div className="flex flex-col my-14 items-center h-screen p-4">
            <form onSubmit={handleSearch} className="relative w-full max-w-md mb-4">
                <input
                    className="w-full py-3 pl-5 pr-12 text-sm bg-white border border-gray-300 rounded-full shadow-sm focus:outline-none"
                    placeholder="Search for an asset here..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button
                    type="submit"
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-blue-500"
                >
                    <FaSearch size={16} />
                </button>
            </form>
            {stockInfo && (
                <div className="w-full max-w-md bg-white border border-gray-200 rounded-lg shadow-md p-4">
                    <h2 className="text-lg font-semibold mb-2">{input} Information</h2>
                    <p className="text-gray-700">Current price: {stockInfo['current price']}</p>
                    <p className="text-gray-700">Day's change: {stockInfo['current price']}</p>
                </div>
            )}
        </div>
    )
}

export default SearchBar
