import SearchBar from '../components/SearchBar'
import { useState, useEffect } from "react";
import axios from 'axios'

const ExplorePage = () => {

    let [trending, setTrending] = useState("")

    useEffect(() => {
        const getTrending = async () => {
            try {
                const response = await axios.get("http://localhost:8000/api/get-trending-stocks/")
                setTrending(response.data["trending stocks"])
            } catch (error) {
                console.error("Error fetching trending stocks:", error);
                setTrending("Server Error.")
            }
        }

        getTrending()
    }, [])

    return (
        <div>
            <div className="container mx-auto px-4 py-8">
  {/* Explore Assets Title */}
  <h1 className="text-4xl font-semibold text-center pt-12 pb-4 text-gray-900">
    Explore Assets
  </h1>
  
  {/* Today's Trending Stocks */}
  <div className="bg-gray-100 rounded-lg shadow-md p-6 mt-8">
    <h2 className="text-2xl font-medium text-center text-gray-700">
      Trending Today
    </h2>
    <p className="text-xl font-light text-center mt-4 text-green-600">
      {trending}
    </p>
  </div>
</div>

            <SearchBar />
        </div>
    )

}

export default ExplorePage
