import { useState, useEffect } from "react";
import { FaSearch } from "react-icons/fa";
import axios from "axios"


const SearchBar = () => {
    let [input, setInput] = useState("")
    let [stockInfo, setStockInfo] = useState("")

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log(input)

        try {
            const response = await axios.get('http://localhost:8000/api/stock-info/', {
                params: { input: input }
            });
            let info_dict = response.data.info
            console.log(info_dict)
            setStockInfo(info_dict)
        } catch (error) {
            console.error("Error fetching stock info:", error);
            setStockInfo("Invalid Query")
        }
    }
    
    useEffect(() => {
        const fetchStockInfo = async (ticker) => {
            try {
                const response = await axios.get('http://localhost:8000/api/stock-info/', {
                    params: { input: ticker } // Sending the input as a query parameter
                });
                setStockInfo(response.data.info); // Update state with fetched stock data
                setInput("NVDA")
            } catch (error) {
                console.error("error fetching data");
            }
        };

        fetchStockInfo("NVDA"); // Trigger search for default stock 'NVDA' on page load
    }, []);

    return (
        // Wrapper for the search bar
        <div>

            <form onSubmit={handleSearch} className="relative w-full">
                <input
                    className="w-full py-4 pl-6 pr-16 text-lg bg-white border border-gray-300 rounded-full shadow-lg focus:outline-none"
                    placeholder="Search for an asset here..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button
                    type="submit"
                    className="absolute right-4 top-1/2 transform -translate-y-1/2 text-blue-500"
                    style={{ zIndex: 3000 }}
                >
                    <FaSearch size={20} />
                </button>
            </form>















        <div className="relative w-full max-w-lg">
            {/* Search Bar Form */}
            <form onSubmit={handleSearch} className="relative">
                <input
                    className="w-full py-4 pl-6 pr-16 text-lg bg-white border border-gray-300 rounded-full shadow-lg focus:outline-none"
                    placeholder="Search for an asset here..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button
                    type="submit"
                    className="absolute right-4 top-1/2 transform -translate-y-1/2 text-blue-500"
                    style={{ zIndex: 3000 }}
                >
                    <FaSearch size={20} />
                </button>
            </form>

            {stockInfo && (
            <div className="bg-white border border-gray-200 rounded-lg p-6 mt-24 ">
                <h2 className="text-xl font-semibold mb-2">Today's {stockInfo['name']} News</h2>
                <h1 className="text-lg mb-4">➤  {stockInfo['news'][0]['title']} <a href={stockInfo['news'][0]['link']} className="text-blue-500 underline text-base">{stockInfo['news'][0]['publisher']}</a></h1>   
                <h1 className="text-lg mb-4">➤  {stockInfo['news'][1]['title']} <a href={stockInfo['news'][1]['link']} className="text-blue-500 underline text-base">{stockInfo['news'][1]['publisher']}</a></h1>                
                <h1 className="text-lg mb-4">➤  {stockInfo['news'][2]['title']} <a href={stockInfo['news'][2]['link']} className="text-blue-500 underline text-base">{stockInfo['news'][2]['publisher']}</a></h1>                
            </div>)
            }

            {/* Stock Information Card */}
            {stockInfo && (
                <div
                    className="fixed right-0 top-0 h-full w-3/4 flex items-center justify-center"
                     // Ensure it appears above other content
                >
                    <div className="w-[48rem] bg-white border border-gray-200 rounded-lg shadow-xl p-6">
                        <h2 className="text-2xl font-semibold mb-2">
                            {stockInfo['ticker']} - {stockInfo['name']}
                        </h2>

                        <h2 className={`text-3xl font-bold ${stockInfo['percent-change'] >= 0 ? 'text-green-700' : 'text-red-700'}`}>
                            ${stockInfo['current-price'].toLocaleString()}
                            <span className="ml-8 text-base">
                                ({stockInfo['percent-change'].toFixed(2)}%)
                            </span>
                        </h2>

                        <p className="text-gray-700 text-lg font-semibold mt-3 underline">Key Statistics</p>
                        <p className="text-gray-700"><b>P/E: </b>{stockInfo['trailing-pe'].toFixed(2)}</p>
                        <p className="text-gray-700"><b>Market Cap:</b> {stockInfo['market-cap'].toLocaleString()}</p>
                        <p className="text-gray-700"><b>EPS:</b> {stockInfo['eps'].toFixed(2)}</p>

                        <p className="text-gray-700 text-lg font-semibold mt-3 underline">About</p>

                        {/* Scrollable "About" Section */}
                        <div className="max-h-64 overflow-y-auto">
                            <p className="text-gray-700">{stockInfo['description']}</p>
                        </div>
                    </div>
                </div>
            )}
        </div>

        </div>
    );
}

export default SearchBar
