  return (
    <div
      className="min-h-screen bg-cover bg-center flex items-start justify-center"
      style={{
        backgroundImage:
          "url('https://images.unsplash.com/photo-1451187580459-43490279c0fa?q=80&w=3872&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')",
      }}
    >
      <Header />

      
      <div className="w-2/3 mt-48">
        <h1 className="text-5xl font-bold text-white mb-8">{user.username.charAt(0).toUpperCase() + user.username.slice(1) }'s Assets</h1>

        
        {/* change to generate one for each resulting stock from api call, now just temp values */}
        <div className="bg-white p-4 rounded-md mx-auto">
            <span className="font-bold">NVDA</span>
            <span className="font-bold ml-16">Current Price</span>
            <span className="font-bold ml-16">Open</span>
            <span className="font-bold ml-16">Close</span>

        </div>
        <div className="bg-white p-4 rounded-md mx-auto mt-6">
            <span className="font-bold">RTX</span>
        </div>
        <div className="bg-white p-4 rounded-md mx-auto mt-6">
            <span className="font-bold">MSFT</span>
        </div>
        <div className="bg-white p-4 rounded-md mx-auto mt-6">
            <span className="font-bold">GOOGL</span>
        </div>


        <h1 className="text-5xl font-bold text-white mb-8 mt-36">Relevant News - use bs4 and ai summarizer</h1>
        <div className="bg-white p-4 rounded-md mx-auto mt-6">
            <h1 className="font-bold">News Headline</h1>
            <p>News article summary --- change into cards for each article found </p>
        </div>

      </div>

    </div>
  );
};

export default DashboardPage;
