import { useState, useEffect, useContext } from "react";
import { FaSearch } from "react-icons/fa";
import axios from "axios";
import AuthContext from '../context/AuthContext';

const SearchBar = () => {
    let { authTokens } = useContext(AuthContext);
    let [input, setInput] = useState("");
    let [stockInfo, setStockInfo] = useState("");

    const handleSearch = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get("http://localhost:8000/api/stock-info/", {
                params: { input: input },
            });
            setStockInfo(response.data.info);
        } catch (error) {
            console.error("Error fetching stock info:", error);
            setStockInfo("Invalid Query");
        }
    };

    const handleAdd = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:8000/api/watchlist-add/", 
                { ticker: stockInfo['ticker'], name: stockInfo['name'] },
                {
                    headers: {
                        Authorization: `Bearer ${authTokens.access}`, 
                        'Content-Type': 'application/json',
                    }
                }
            );
        } catch (error) {
            console.error("Error adding to watchlist");
        }
    }

    useEffect(() => {
        const fetchStockInfo = async (ticker) => {
            try {
                const response = await axios.get("http://localhost:8000/api/stock-info/", {
                    params: { input: ticker },
                });
                setStockInfo(response.data.info);
                setInput("NVDA");
            } catch (error) {
                console.error("Error fetching data");
            }
        };

        fetchStockInfo("NVDA");
    }, []);

    return (
        <div className="flex flex-col items-center">
            {/* SearchBar */}
            <form onSubmit={handleSearch} className="relative mb-4 w-1/2">
                <input
                    className="w-full py-4 pl-6 pr-16 text-lg bg-white border border-gray-300 rounded-full shadow-lg focus:outline-none"
                    placeholder="Search for an asset here..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button
                    type="submit"
                    className="absolute right-4 top-1/2 transform -translate-y-1/2 text-blue-500"
                >
                    <FaSearch size={20} />
                </button>
            </form>

            {/* News Section */}
            {stockInfo && (
                <div className="bg-white border border-gray-200 rounded-lg p-6 w-3/4 mt-4 max-h-64 overflow-y-auto">
                    <h2 className="text-xl font-semibold mb-2">Today's {stockInfo["name"]} News</h2>
                    {stockInfo["news"].slice(0, 3).map((newsItem, index) => (
                        <h1 key={index} className="text-lg mb-4">
                            âž¤ {newsItem["title"]}{" "}
                            <a
                                href={newsItem["link"]}
                                className="text-blue-500 underline text-base"
                            >
                                {newsItem["publisher"]}
                            </a>
                        </h1>
                    ))}
                </div>
            )}
        </div>
    );
};

export default SearchBar;
