import { useState } from "react";
import { FaSearch } from "react-icons/fa";
import axios from "axios"


const SearchBar = () => {
    let [input, setInput] = useState("")
    let [stockInfo, setStockInfo] = useState("")

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log(input)

        try {
            // Send request to the backend
            const response = await axios.get('http://localhost:8000/api/get-stock-info/', {
                params: { input: input } // Sending the input as a query parameter
            });

            // Log the response data
            console.log(response.data.info);
            setStockInfo(response.data.info)
        } catch (error) {
            console.error("Error fetching stock info:", error);
        }

        // send request to the backend, retrieve json response with stock info
    }

    return (
        <div className="flex justify-center items-center h-screen">
            <form onSubmit={handleSearch} className="relative w-full max-w-md">
                <input
                    className="w-full py-3 pl-5 pr-12 text-sm bg-white border border-gray-300 rounded-full shadow-sm focus:outline-none"
                    placeholder="Search for an asset here..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <button
                    type="submit"
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-blue-500"
                >
                    <FaSearch size={16} />
                </button>
            </form>
            <h1>{stockInfo}</h1>
        </div>
    )
}

export default SearchBar
